        return new Source("{% extends 'base.html.twig' %}

{% block title %}Post{% endblock %}

{% block body %}

    <div class=\"container col-md-8 m-auto\">
        <h1>{{post.title}}</h1>
        <p>Data: {{post.date | date(\"d/m/y\")}}</p>
        <h3>{{ post.resume }}</h3>
        <img src=\"{{ '/uploads/' ~ post.image }}\" alt=\"\">
        <p>{{post.content}}</p>
    
        <a class=\"btn btn-primary\" href=\"{{ path('post_index') }}\">Todas as noticias</a>
        <a class=\"btn btn-primary\" href=\"{{ path('home') }}\">Noticias em destaque</a>

        {% if is_granted('ROLE_ADMIN') %}
            <a href=\"{{ path('post_edit', {'id': post.id}) }}\">edit</a>

            {{ include('post/_delete_form.html.twig') }}
        {% endif %}
    </div>
{% endblock %}
